import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios'
import '../../Utils/style.css';
import "../../Utils/css/header.css"
import "../../Utils/css/bootstrap.min.css"
import "../../Utils/css/bootstrap-datepicker.css"
import profile from "../../images/profile.png"
import pencil from "../../images/pencil.png"
import DatePicker from "react-datepicker";
import { Network } from '../../Services/Api';
import { ToastContainer, toast } from 'react-toastify';
import "react-datepicker/dist/react-datepicker.css";
import HeaderComponents from "../../Components/header"
import FooterComponents from "../../Components/footer"

const Gender = [
  {
    "name": "Male",
    "value": "M"
  },
  {
    "name": "Female",
    "value": "F"
  },
  {
    "name": "Others",
    "value": "O"
  },
]

const country = [
  {
    "name": "Unitied Kingdom",
    "value": "Unitied Kingdom"
  },
  {
    "name": "South Africa",
    "value": "South Africa"
  },
  {
    "name": "Australia",
    "value": "Australia"
  },
]

function EditProfileComponents(props) {
  const [startDate, setStartDate] = useState(new Date());
  const [genderValue, setGender] = useState("Male")
  const [firstName, setfirstName] = useState("")
  const [lastName, setlastName] = useState("")
  const [about, setAbout] = useState("")
  const [profileImage, setProfiles] = useState("")
  const [countryValue, setCountry] = useState("South Africa")
  const [user, setUserData] = useState("")
  const [errors, setError] = useState({})

  useEffect(() => {

    const userLocal = JSON.parse(localStorage.getItem('user'));
    console.log("********************userData after login--->", userLocal)
    setUserData(userLocal)
    setTimeout(() => {
      getProfile()
    },500);

  }, []);

  const inputEl = useRef(null);
  function handleClick() {
    // console.log("98888888", inputEl)
    inputEl.current.click();
  }


  const handleGender = (e) => {
    setGender(e.target.value)
  }
  const handleCountry = (e) => {
    setCountry(e.target.value)
  }
  const ExampleCustomInput = ({ value, onClick }) => (
    <div onClick={onClick} style={{ width: 350 }}>
      <span>{value}</span>
    </div>
  );

  const sendDate = (val) => {
    var today = new Date(val);
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
    var yyyy = today.getFullYear();
    today = dd + '/' + mm + '/' + yyyy;
    return today
  }

  const getProfile = async (event) => {
    const userLocal = JSON.parse(localStorage.getItem('user'));
    var obj = {
      authToken: userLocal ?  userLocal.token : "",
    };
    Network('user/profile', 'get', obj)
      .then((res) => {
        console.log("res success verify otp--->",res);
        if (res.status == true) {
          setfirstName(res.result.user.firstName)
          setlastName(res.result.user.lastName)
          // setStartDate(res.result.dob)
          setCountry(res.result.country)
          setGender(res.result.gender == "M" ? "Male" : "Female")
          setProfiles(res.result.profile_url)
        } else {
          toast.error(res.message)
        }
      })
      .catch((error) => {
        console.log("error===>", error)
      });

  };


  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("RES: ",`Bearer ${user.token}`);
    let formData = new FormData();
    formData.append("gender", genderValue == "Male" ? "M" : "F");
    formData.append("country", countryValue);
    formData.append("dob", sendDate(startDate));
    formData.append("about", about);
    formData.append("profile", profileImage);
    formData.append("firstName", firstName);
    formData.append("lastName", lastName);

    const end_point = "http://nodeserver.mydevfactory.com:1443/user/profile";
    const headers = {
    "Content-Type":"multipart/form-data",
    Authorization:`Bearer ${user.token}`
    };

    return axios({
    method: "PUT",
    url: end_point,
    data: formData,
    headers,
    })
    .then(function (response) {
    //handle success
    console.log("RES: ", response,`Bearer ${user.token}`);
    })
    .catch(function (err) {
    //handle error
    console.log("ERR: ", err.response.data);
    });
    };

   const doValidation = (event) => {
    event.preventDefault();
      if (firstName == "") {
        toast.warning("Please enter your first name.")
        return
      }
      if (lastName == "") {
        toast.warning("Please enter your last name.")
        return
      }
      if (about == "") {
        toast.warning('Please enter about us')
        return
      }
      handleSubmit()
    }


     /******************For files***********************************/
     const handleChange = event => {
      console.log("URL.createObjectURL(event.target.files[0])---->", URL.createObjectURL(event.target.files[0]));
      setProfiles(URL.createObjectURL(event.target.files[0]))
      // uploadImage(URL.createObjectURL(event.target.files[0]))

  };

  return (
    <div>
      <HeaderComponents flag={true} as={props} headerText="PROFILE"/>
      <section class="home-profile">
        <div class="container">
          <div class="row">
            <div class="col-lg-6 col-md-10 col-10 mx-auto profile-sec">
              <form onSubmit={doValidation}>
                <figure class="profile">
                  {
                    profileImage ? 
                    <img src={`${"http://nodeserver.mydevfactory.com:1443/"}${profileImage}`} class="img-fluid" alt=""  style={{width:100,height:100,borderRadius:50}}/> 
                    :
                    <img src={profile} class="img-fluid" alt="" />
                  }

                </figure>
                <img src={pencil} class="img-fluid pencil" alt="" onClick={() => handleClick()} />
                <input
                  type="file"
                  style={{ display: "none" }}
                  className="form-control"
                  name="file"
                  onChange={handleChange} 
                  ref={inputEl}
                // value=""
                />
                <div class="form-group input-group">
                  <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="First Name" onChange={(event) => setfirstName(event.target.value)} value={firstName} name="firstName" />
                  <div class="input-group-append">
                    <span class="input-group-text"><i class="fa fa-user-o" aria-hidden="true"></i></span>
                  </div>
                </div>
                <span style={{ color: "red", fontSize: 12 }}>
                  {errors.firstName}
                </span>
                <div class="form-group input-group">
                  <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Last Name" onChange={(event) => setlastName(event.target.value)} value={lastName} name="lastName" />
                  <div class="input-group-append">
                    <span class="input-group-text"><i class="fa fa-user-o" aria-hidden="true"></i></span>
                  </div>
                </div>
                <span style={{ color: "red", fontSize: 12 }}>
                  {errors.lastName}
                </span>
                <div class="form-group">
                  <div class="dates input-group">
                    {/* <input type="text" class="form-control" id="usr1" name="event_date" placeholder="D.O.B" autocomplete="off" /> */}
                    <div className="form-control">
                      <DatePicker
                        selected={startDate}
                        onChange={date => setStartDate(date)}
                        customInput={<ExampleCustomInput />}
                      />
                    </div>

                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-calendar-o fa-1x" aria-hidden="true"></i></span>
                    </div>
                  </div>
                </div>
                <div class="form-group input-group">
                  <select onChange={(e) => handleGender(e)} name="genderName">
                    {
                      Gender.map((item) => {
                        return (
                          <option value={item.value} name="genderName">{item.name}</option>
                        )
                      })
                    }
                  </select>
                </div>
                <div class="form-group input-group">
                  <select onChange={(e) => handleCountry(e)} name="countryName">
                    {
                      country.map((item) => {
                        return (
                          <option value={item.value} name="countryName">{item.name}</option>
                        )
                      })
                    }
                  </select>
                </div>

                <div class="form-group">
                  <textarea class="form-control" id="exampleFormControlTextarea1" rows="2" placeholder="Type your comment" onChange={(event) => setAbout(event.target.value)} value={about} name="about"></textarea>
                </div>
                <span style={{ color: "red", fontSize: 12 }}>
                  {errors.about}
                </span>
                <div class="row">
                  <div class="col-md-12">
                    <div class="save-btn">
                      <button type="submit">Save</button>
                    </div>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>


      <FooterComponents />
    </div >
  );
