import React, { useState } from 'react';
import Modal from "react-bootstrap/Modal";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory
} from "react-router-dom";
import '../../Utils/css/style.css';
import '../../Utils/css/responsive.css';
import "../../Utils/css/bootstrap.min.css"
import "../../Utils/css/bootstrap-datepicker.css"
import modalClose from "../../images/modal-close.png"
import Home from "../../Screens/Home"
import OtpInput from 'react-otp-input';

function VerifyOtpComponents(props) {

  const [otp, setOtp] = useState('')


  const handleChange = otp => {
    props.handleChange(otp)
    setOtp(otp)
  }

  return (
    <>
      <Home />
      <div class="modal" id="myModallogin" >
        <div class="modal-dialog custom-modal">
          <div class="modal-content">

            <div class="modal-header">
              <h4 class="modal-title">verify-otp</h4>
              <button type="button" class="close" data-dismiss="modal">
                <img src={modalClose} alt="" />
              </button>
            </div>

            <div class="modal-body">
              <form>
                <div style={{ alignItems: 'center', display: "flex", justifyContent: 'center', height: 200 }}>
                  <OtpInput
                    value={otp}
                    onChange={handleChange}
                    shouldAutoFocus={false}
                    numInputs={4}
                    placeholder={"000000"}
                    containerStyle={{ borderColor: 'red', borderWidth: 2, borderRadius: 10 }}
                    inputStyle={{
                      width: '3rem',
                      height: '3rem',
                      margin: '20px 1rem',
                      fontSize: '2rem',
                      borderRadius: 4,
                      border: '2px solid rgba(0,0,0,0.3)',
                    }}
                    separator={<span style={{ fontSize: 20 }}> - </span>}
                  />
                </div>
                <div class="btn-set">
                  <button type="submit" class="btn btn-deflt">Submit</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default VerifyOtpComponents;
