import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios'
import '../../Utils/style.css';
import "../../Utils/css/header.css"
import "../../Utils/css/bootstrap.min.css"
import "../../Utils/css/bootstrap-datepicker.css"
import become_host from "../../images/become_host.jpg"
import view from "../../images/view.jpg"
import download from "../../images/download.jpg"
import DatePicker from "react-datepicker";
import { Network } from '../../Services/Api';
import { ToastContainer, toast } from 'react-toastify';
import "react-datepicker/dist/react-datepicker.css";
import HeaderComponents from "../../Components/header"
import FooterComponents from "../../Components/footer"
import FileViewer from 'react-file-viewer';

const type = 'docx'
function BecomeHostComponents(props) {
  const [files, setFiles] = useState('')
  const [fileDownload, setFilesDownload] = useState('')

  useEffect(() => {

    const filesLocal = localStorage.getItem('file');
    if(filesLocal !== null){
      console.log("filesLocal---->", filesLocal);
      setFiles(filesLocal)
      // setFilesDownload(filesLocal)
    }

  }, []);


  /*************************************************************/
  const handleUpload = async (event) => {
    console.log("URL.createObjectURL(event.target.files[0])---->", URL.createObjectURL(event.target.files[0]));
    setFiles(URL.createObjectURL(event.target.files[0]))
    setFilesDownload(URL.createObjectURL(event.target.files[0]))
    localStorage.setItem('file', URL.createObjectURL(event.target.files[0]));
  }

  /************************************************************/

  const handleView = () => {
    console.log('files====>', files);
    return (
      <FileViewer
        fileType={type}
        filePath={files}
        // errorComponent={CustomErrorComponent}
        onError={(event) => console.log("error----", event)}
      />
    )
  }


  return (
    <div>
      <HeaderComponents flag={true} as={props} headerText="BECOME HOST" />
      <section class="become-host-one">
        <div class="container">
          <div class="row">
            <div class="col-md-6">
              <img src={become_host} class="img-fluid" alt="" />
            </div>
            <div class="col-md-6">
              <h5>Contact Now</h5>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Quis ipsum suspendisse ultrices gravida. Risus commodo viverra maecenas accumsan lacus vel facilisis</p>
              {/* <iframe src = {files} width='700' height='550' allowfullscreen webkitallowfullscreen></iframe> */}

              <form>
                <div class="form-group">
                  <input type="email" class="form-cont" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="First Name" />
                </div>
                <div class="form-group">
                  <input type="email" class="form-cont" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Last Name" />
                </div>
                <div class="form-group">
                  <input type="email" class="form-cont" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Email" />
                </div>
                <div class="form-group">
                  <input type="email" class="form-cont" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Phone Number" />
                </div>
                <h4 ><a href={files}><img src={view} class="img-fluid" alt="" /> View Form</a> <span><a href={files}><img src={download} class="img-fluid" alt="" /> Download Form</a></span></h4>
                <div class="upload">
                  <span><input type="file" name="file" id="file" class="inputfile" onChange={handleUpload} />
                    <label for="file"><i class="fa fa-upload" aria-hidden="true"></i> Upload Form</label></span>
                  <span><button type="submit">Submit</button></span>
                  <span><input type="file" name="file" id="file" class="inputfile" />
                    <label for="file"><i class="fa fa-upload" aria-hidden="true"></i> Upload Image</label></span>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>


      <FooterComponents />
    </div >
  );
}

export default BecomeHostComponents;
