import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios'
import '../../Utils/style.css';
import "../../Utils/css/header.css"
import "../../Utils/css/bootstrap.min.css"
import "../../Utils/css/bootstrap-datepicker.css"
import profile from "../../images/profile.png"
import pencil from "../../images/pencil.png"
import DatePicker from "react-datepicker";
import { Network } from '../../Services/Api';
import { ToastContainer, toast } from 'react-toastify';
import "react-datepicker/dist/react-datepicker.css";
import HeaderComponents from "../../Components/header"
import FooterComponents from "../../Components/footer"

const Gender = [
  {
    "name": "Male",
    "value": "M"
  },
  {
    "name": "Female",
    "value": "F"
  },
  {
    "name": "Others",
    "value": "O"
  },
]

const country = [
  {
    "name": "Unitied Kingdom",
    "value": "Unitied Kingdom"
  },
  {
    "name": "South Africa",
    "value": "South Africa"
  },
  {
    "name": "Australia",
    "value": "Australia"
  },
]

function EditProfileComponents(props) {
  const [input, setInput] = useState({})
  const [startDate, setStartDate] = useState(new Date());
  const [genderValue, setGender] = useState("Male")
  const [firstName, setfirstName] = useState("")
  const [lastName, setlastName] = useState("")
  const [about, setAbout] = useState("")
  const [profile, setProfiles] = useState("")
  const [countryValue, setCountry] = useState("South Africa")
  const [user, setUserData] = useState("")
  const [errors, setError] = useState({})

  useEffect(() => {

    const userLocal = JSON.parse(localStorage.getItem('user'));
    console.log("********************userData after login--->", userLocal)
    setUserData(userLocal)
    getProfile()
  }, []);

  const inputEl = useRef(null);
  function handleClick() {
    // console.log("98888888", inputEl)
    inputEl.current.click();
  }


  const handleGender = (e) => {
    setGender(e.target.value)
  }
  const handleCountry = (e) => {
    setCountry(e.target.value)
  }
  const ExampleCustomInput = ({ value, onClick }) => (
    <div onClick={onClick} style={{ width: 350 }}>
      <span>{value}</span>
    </div>
  );

  const sendDate = (val) => {
    var today = new Date(val);
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
    var yyyy = today.getFullYear();
    today = dd + '/' + mm + '/' + yyyy;
    return today
  }

  const getProfile = async (event) => {
    const token = await user.token;
    console.log("hellooooo",token)
    var obj = {
      authToken: token,
    };
   await Network('user/profile', 'get', obj)
      .then(async(res) => {
        console.log("res success verify otp--->",await res);
        if (res.status == true) {
          // setfirstName(res.result.user.firstName)
          // setlastName(res.result.user.lastName)
          // setStartDate(res.result.dob)
          //  setcountry(res.result.country)
          // setgender(res.result.gender == "M" ? "Male" : "Female")
          // setProfiles(res.result.firstName)
        } else {
          toast.error(res.message)
        }
      })
      .catch((error) => {
        console.log("error===>", error)
      });

  };


  // const handleSubmit = (event) => {

  //   let formData = new FormData();
  //   formData.append("gender", genderValue);
  //   formData.append("country", countryValue);
  //   formData.append("dob", sendDate(startDate));
  //   formData.append("about", input['about']);
  //   formData.append("profile", input['file']);
  //   formData.append("firstName",input['firstName'] );
  //   formData.append("lastName", input['lastName']);

  //   for(var key of formData.entries()){
  //       console.log("key",key[0],key[1])
  //   }
  //   axios.put(`http://nodeserver.mydevfactory.com:1443/user/profile`, formData,{
  //       headers: {
  //         'Content-Type': 'multipart/form-data',
  //          Accept: "application/json",
  //         'Authorization': `Bearer ${user}`,
  //       },
  //     })
  //     .then((response) => {
  //       console.log("erwewrewrwerwr",JSON.stringify(response))
  //       // if (response.status == true) {
  //       //   console.log("erwewrewrwerwr", response)
  //       // }
  //       // else {
  //       //   console.log("erwewrewrwerwr", response)
  //       // }
  //     })
  //     .catch((error) => {
  //       console.error(error);
  //     });
  //     event.preventDefault();
  // }

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("RES: ",`Bearer ${user.token}`);
    let formData = new FormData();
    formData.append("gender", genderValue == "Male" ? "M" : "F");
    formData.append("country", countryValue);
    formData.append("dob", sendDate(startDate));
    formData.append("about", about);
    formData.append("profile", input["file"]);
    formData.append("firstName", input["firstName"]);
    formData.append("lastName", input["lastName"]);

    const end_point = "http://nodeserver.mydevfactory.com:1443/user/profile";
    const headers = {
    "Content-Type":"multipart/form-data",
    Authorization:`Bearer ${user.token}`
    };

    return axios({
    method: "PUT",
    url: end_point,
    data: formData,
    headers,
    })
    .then(function (response) {
    //handle success
    console.log("RES: ", response,`Bearer ${user.token}`);
    })
    .catch(function (err) {
    //handle error
    console.log("ERR: ", err.response.data);
    });
    };



  const validate = () => {
    let input1 = input;
    let errors = {};
    let isValid = true;

    if (!input1["firstName"]) {
      isValid = false;
