import React, { useState,useEffect } from 'react';
import Modal from "react-bootstrap/Modal";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import countryCode from './../Components/Comman/country.json'
import HeaderComponents from "../Components/header"
import FooterComponents from "../Components/footer"
import OtpComponent from "../Components/verifyOtp"
import '../Utils/style.css';
import "../Utils/css/header.css"
import "../Utils/css/bootstrap.min.css"
import "../Utils/css/bootstrap-datepicker.css"
import Imnot from "../images/imnot.png"
import { Network } from './../Services/Api';
import { ToastContainer, toast } from 'react-toastify';


const language = [
  {
    "name": "english",
    "value": "english"
  },
  {
    "name": "french",
    "value": "french"
  },
  {
    "name": "portuguese",
    "value": "portuguese"
  },
  {
    "name": "swahili",
    "value": "swahili"
  },
]

function SignUpComponents(props) {
  const [input, setInput] = useState({})
  const [country_code, setCountry_code] = useState("+93")
  const [errors, setError] = useState({})
  const [otp, setOtp] = useState('')
  const [otpShow, setOtpShow] = useState(false)
  const [signUpShow, setsignUpShow] = useState(true)
  const [userData, setUserData] =useState('customer')
  const [languageValue, setLanguage] = useState("english")



  //.........for on change mether.............


  const handleChange = (event) => {
    let input1 = input;
    input1[event.target.name] = event.target.value;
    console.log(input1)
    setInput(input1)


  }

  const handleUserType = (e) => {
      setUserData(e.target.value)

  }

  const handleLaguage = (e)=>{
    setLanguage(e.target.value)
  }
  //.........Login submit............

  const handleSubmit = (event) => {

    if (validate()) {

      const obj = {
        "email": input['email'],
        "password": input['password'],
        "apptype": "ANDROID",
        "fname": input['firstName'],
        "lname": input['lastName'],
        "phone_no": input['mobile'],
        "country_code":country_code,
        "language": languageValue,
        "user_type": userData
      }
      console.log("res success register--->", obj);

      Network('register', 'post', obj)
        .then(async (res) => {
          console.log("res success register--->", res);
          if (res.response_code == 2000) {
            toast.success(res.response_message)
            setsignUpShow(false)
            setOtpShow(true)
          }else{
            toast.error(res.response_message)
          }
        })
        .catch((error) => {
          console.log("error===>", error)
        });

    }
    event.preventDefault();
  }

  const validate = () => {
    let input1 = input;
    let errors = {};
    let isValid = true;

    if (!input1["firstName"]) {
      isValid = false;
      errors["firstName"] = "Please enter your first name.";
    }

    if (!input1["lastName"]) {
      isValid = false;
      errors["lastName"] = "Please enter your last name.";
    }

    if (!input1["mobile"]) {
      isValid = false;
      errors["mobile"] = "Please enter your mobile number.";
    }

    if (!input1["confPassword"]) {
      isValid = false;
      errors["confPassword"] = "Please enter your confirm password.";
    }

    if (!input1["email"]) {
      isValid = false;
      errors["email"] = "Please enter your email Address.";
    }

    if (typeof input1["email"] !== "undefined") {

      var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
      if (!pattern.test(input1["email"])) {
        isValid = false;
        errors["email"] = "Please enter valid email address.";
      }
    }

    if (!input1["password"]) {
      isValid = false;
      errors["password"] = "Please enter your password.";
    }

    if (input1["confPassword"] == input1["password"]) {
      isValid = true
    } else {
      isValid = false;
      errors["confPassword"] = "Passwords do not match.";
    }
    if (!input1["terms"]) {
      isValid = false;
      errors["terms"] = "Please select terms & conditions";
    }

    setError(errors)

    return isValid;
  }


  const handleSubmitOtp = () => {
    var obj = {
      "email": input['email'],
      'verification_code': otp
    }

    Network('verify-account', 'post', obj)
      .then(async (res) => {
        console.log("res success verify otp--->", res);
        if (res.response_code == 2000) {
          toast.success(res.response_message)
          setOtpShow(false)
          props.history.push("/")
        } else {
          toast.error(res.response_message)
        }
      })
      .catch((error) => {
        console.log("error===>", error)
      });
  }



  return (
    <>
      <div>
        {/* login Modal */}
        <HeaderComponents />
        <OtpComponent
                show={otpShow}
                close={() => setOtpShow(false)}
                handleChange={(val) => setOtp(val)}
                onSubmitOtp={() => handleSubmitOtp()}
            />

        <Modal class="modal fade" id="exampleModal-two" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true" show={signUpShow}>
          <div class="modal-dialog" role="document">
            <div class="modal-content">
              <div class="modal-header">
                <button type="button" class="close" data-dismiss="modal" aria-label="Close" onClick={() => props.history.push("/")}>
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div class="login-modal">
                <h2>create an account</h2>
                <h5>You and friend always conected</h5>
                <form onSubmit={handleSubmit}>
                  <div class="form-group input-group">
                    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="First Name" onChange={handleChange} name="firstName" />
                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-user-o" aria-hidden="true"></i></span>
                    </div>
                  </div>
                  <span style={{ color: "red", fontSize: 12 }}>
                    {errors.firstName}
                  </span>
                  <div class="form-group input-group">
                    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Last Name" onChange={handleChange} name="lastName" />
                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-user-o" aria-hidden="true"></i></span>
                    </div>
                  </div>
                  <span style={{ color: "red", fontSize: 12 }}>
                    {errors.lastName}
                  </span>
                  <div class="form-group input-group">
                    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Email" onChange={handleChange} name="email" />
                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-envelope-o" aria-hidden="true"></i></span>
                    </div>
                  </div>
                  <span style={{ color: "red", fontSize: 12 }}>
                    {errors.email}
                  </span>
                  <div class="form-group input-group">
                    <div class="input-group-prepend">
                      <select onChange={(value)=>setCountry_code(value.target.value)} name="numberCode" >
                        {
                          countryCode.map((item) => {
                            return (
                              <option value={item.dial_code} name="numberCode">{item.dial_code}</option>
                            )
                          })
                        }
                      </select>
                    </div>
                    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"
                      placeholder="Mobile Number" onChange={handleChange} name='mobile' />
                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-mobile fa-2x" aria-hidden="true"></i></span>
                    </div>
                  </div>
                  <span style={{ color: "red", fontSize: 12 }}>
                    {errors.mobile}
                  </span>
                  <div class="form-group input-group">
                    <select onChange={(e)=> handleLaguage(e)} name="languageName">
                    {
                        language.map((item) => {
                            return (
                              <option value={item.value} name="languageName">{item.name}</option>
                            )
                          })
                        }
                    </select>
                  </div>

                  <div class="form-group input-group">
                    <input type="password" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Password" onChange={handleChange} name='password' />
                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-eye" aria-hidden="true"></i></span>
                    </div>
                  </div>
                  <span style={{ color: "red", fontSize: 12 }}>
                    {errors.password}
                  </span>

                  <div class="form-group input-group">
                    <input type="password" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Confirm Password" onChange={handleChange} name='confPassword'/>
                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-eye" aria-hidden="true"></i></span>
                    </div>
                  </div>
                  <span style={{ color: "red", fontSize: 12 }}>
                                    {errors.confPassword}
                                </span>

                  <div class="row">
                    <div class="col-md-12">
                      <div class="radio">
                        <label class="radio1">
                          <input type="radio" id="option2" name="radio-group" checked onChange={(e)=>handleUserType(e)} value='customer'/> Customer
                              </label>
                        <label class="radio2">
                          <input type="radio" id="option2" name="radio-group" onChange={(e)=>handleUserType(e)} value='manager'/> Hotel/Apartment Owner
                              </label>
                        {/* <label class="radio3">
                          <input type="radio" name="options" id="option3" autocomplete="off" onChange={handleChange} name='userType' /> Guest
                              </label> */}
                      </div>
                      <h4><input type="checkbox" class="form-check-input" id="exampleCheck1" onChange={handleChange} name='terms' /> I agree to the <a href="#">Terms of Service</a> and <a href="#">Privary Policy</a></h4>
                      <span style={{ color: "red", fontSize: 12 }}>
                                    {errors.terms}
                                </span>
                      <div class="sign-in sign-two">
                        <button type="submit">Sign Up</button>
                      </div>
                      <h4>Don’t have an Account? <a href="#" > <Link to="/login">Sign In</Link></a></h4>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </Modal>
        <FooterComponents />
        {/* login Modal */}

      </div>
    </>
  );
}

export default SignUpComponents;
