import React, { useState } from 'react';
import Modal from "react-bootstrap/Modal";
import '../Utils/style.css';
import "../Utils/css/header.css"
import "../Utils/css/bootstrap.min.css"
import OtpInput from 'react-otp-input';
import '../Utils/style.css';
import "../Utils/css/header.css"
import "../Utils/css/bootstrap.min.css"
import { ToastContainer, toast } from 'react-toastify';


function ResetPassword(props) {
    const [otp, setOtp] = useState('')


    const handleChange = otp => {
        props.handleChangeOtp(otp)
        setOtp(otp)
    }

    return (
        <>
            <Modal show={props.show} className="modal fade" id="exampleModal-two" aria-labelledby="exampleModalLabel" role="dialog" style={{height:"100%"}}>
                <div className="modal-header">
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close" onClick={() => props.onClick()}>
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div className="modal-body">
                    <h2>reset password</h2>
                    <div class="form-group input-group" >
                    <input  type="password" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="new Password" onChange={(e)=>props.handleChangepassword(e)} name={props.newPassword}/>
                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-eye" aria-hidden="true"></i></span>
                    </div>
                  </div>
                    <div class="form-group input-group" >
                    <input  type="password" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="new Confirm Password" onChange={(e)=>props.handleChangeConPassword(e)} name={props.newConfirmPassword}/>
                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-eye" aria-hidden="true"></i></span>
                    </div>
                  </div>
                    <div className="sign-in sign-two">
                        {/* <button type="submit" onClick={()=> props.onSubmitOtp()}>Save</button> */}
                        <button onClick={()=> props.onSubmitReset()}>Save</button>
                    </div>
                </div>
            </Modal>
        </>
    )
}
export default ResetPassword;
