import React, { useState, useRef } from 'react';
import '../../Utils/style.css';
import "../../Utils/css/header.css"
import "../../Utils/css/bootstrap.min.css"
import "../../Utils/css/bootstrap-datepicker.css"
import profile from "../../images/profile.png"
import pencil from "../../images/pencil.png"
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import HeaderComponents from "../../Components/header"
import FooterComponents from "../../Components/footer"

const Gender = [
  {
    "name": "Male",
    "value": "M"
  },
  {
    "name": "Female",
    "value": "F"
  },
  {
    "name": "Others",
    "value": "O"
  },
]

function EditProfileComponents(props) {
  const [input, setInput] = useState({})
  const [startDate, setStartDate] = useState(new Date());
  const [genderValue, setGender] = useState("Male")
  const [errors, setError] = useState({})



  const inputEl = useRef(null);
  function handleClick() {
    console.log("98888888", inputEl)
    inputEl.current.click();
  }

  const handleChange = (event) => {
    let input1 = input;
    input1[event.target.name] = event.target.value;
    console.log(input1)
    setInput(input1)


  }
  const ExampleCustomInput = ({ value, onClick }) => (
    <div onClick={onClick} style={{ width: 350 }}>
      <span>{value}</span>
    </div>
  );


  return (
    <div>

      <HeaderComponents flag={true} />
      <section class="home-profile">
        <div class="container">
          <div class="row">
            <div class="col-lg-6 col-md-10 col-10 mx-auto profile-sec">
              <form>
                <figure class="profile">
                  <img src={profile} class="img-fluid" alt="" />
                </figure>
                <img src={pencil} class="img-fluid pencil" alt="" onClick={() => handleClick()} />
                <input
                  type="file"
                  style={{ display: "none" }}
                  className="form-control"
                  name="file"
                  onChange={handleChange}
                  ref={inputEl}
                // value=""
                />
                <div class="form-group input-group">
                  <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="First Name" />
                  <div class="input-group-append">
                    <span class="input-group-text"><i class="fa fa-user-o" aria-hidden="true"></i></span>
                  </div>
                </div>
                <span style={{ color: "red", fontSize: 12 }}>
                    {errors.firstName}
                  </span>
                <div class="form-group input-group">
                  <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Last Name" />
                  <div class="input-group-append">
                    <span class="input-group-text"><i class="fa fa-user-o" aria-hidden="true"></i></span>
                  </div>
                </div>
                <span style={{ color: "red", fontSize: 12 }}>
                    {errors.lastName}
                  </span>
                <div class="form-group">
                  <div class="dates input-group">
                    {/* <input type="text" class="form-control" id="usr1" name="event_date" placeholder="D.O.B" autocomplete="off" /> */}
                    <div className="form-control">
                      <DatePicker
                        selected={startDate}
                        onChange={date => setStartDate(date)}
                        customInput={<ExampleCustomInput />}
                      />
                    </div>

                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-calendar-o fa-1x" aria-hidden="true"></i></span>
                    </div>
                  </div>
                </div>
                <div class="form-group input-group">
                  <select onChange={(e)=> handleLaguage(e)} name="genderName">
                  {
                        Gender.map((item) => {
                            return (
                              <option value={item.value} name="genderName">{item.name}</option>
                            )
                          })
                        }
                  </select>
                </div>
                <div class="form-group input-group">
                  <select>
                    <option>Country</option>
                    <option>Country 1</option>
                    <option>Country 2</option>
                  </select>
                </div>

                <div class="form-group">
                  <textarea class="form-control" id="exampleFormControlTextarea1" rows="2" placeholder="Type your comment"></textarea>
                </div>

                <div class="row">
                  <div class="col-md-12">
                    <div class="save-btn">
                      <button type="submit">Save</button>
                    </div>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>


      <FooterComponents />
    </div >
  );
}

export default EditProfileComponents;
