import React, { useState } from 'react';
import Modal from "react-bootstrap/Modal";
import OtpComponent from '../Components/verifyOtp'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import HeaderComponents from "../Components/header"
import FooterComponents from "../Components/footer"
import '../Utils/style.css';
import "../Utils/css/header.css"
import "../Utils/css/bootstrap.min.css"
import "../Utils/css/bootstrap-datepicker.css"
import Imnot from "../images/imnot.png"
import { loginUser } from "./../Redux/Actions/auth"
import { Network } from './../Services/Api';



function ForgetPasswordComponents(props) {

  const [input, setInput] = useState({})
  const [errors, setError] = useState({})
  const [show, setShow] = useState(false);
  const [showSingup, setShowSingup] = useState(false);
  const [otpShow, setOtpShow] = useState(false)
  const [otp, setOtp] = useState('')
  const [email, setEmail] = useState('')
  //.........for on change mether.............

  const handleChange = (event) => {
      let input1 = input;
      input1[event.target.name] = event.target.value;
      console.log(input1)
      setInput(input1)
  }

  //.........Login submit............

  const handleSubmit = (event) => {

      if (validate()) {
          setEmail(input['email'])
          var obj = {
              "email": input['email']
          }
          console.log("obj for get--->", obj)

          Network('forgot-password', 'post', obj)
              .then(async (res) => {
                  console.log("res success generate otp forgot pass--->", res);
                  if (res.response_code == 2000) {
                      props.history.push("/resetpassword")
                  } else {

                  }
              })
              .catch((error) => {
                  console.log("error===>", error)
              });
      }

      event.preventDefault();
  }

  const validate = () => {
      let input1 = input;
      let errors = {};
      let isValid = true;

      if (!input1["email"]) {
          isValid = false;
          errors["email"] = "Please enter your email Address.";
      }

      if (typeof input1["email"] !== "undefined") {
          var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
          if (!pattern.test(input1["email"])) {
              isValid = false;
              errors["email"] = "Please enter valid email address.";
          }
      }
      setError(errors)
      return isValid;
  }



  const onSubmitOtp = () =>{
      var obj = {
          "email": email,
          'forgot_password_otp': otp
      }

      Network('verify-fp-otp', 'post', obj)
          .then(async (res) => {
              console.log("res success verify otp--->", res);
              if (res.response_code == 2000) {
                toast.success(res.response_message)
              } else {

              }
          })
          .catch((error) => {
              console.log("error===>", error)
          });
  }


    return (
        <>
        <div>
            {/* login Modal */}
            <HeaderComponents />
            <OtpComponent
                show={otpShow}
                onClick={() => setOtpShow(false)}
                handleChange={(val) => setOtp(val)}
                onSubmitOtp={() => onSubmitOtp()}
            />

            <Modal class="modal fade" id="exampleModal-three" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true" show={true}>
              <div class="modal-dialog" role="document">
                <div class="modal-content">
                  <div class="modal-header">
                    <button type="button" class="close" data-dismiss="modal" aria-label="Close" onClick={() => props.history.push("/")}>
                      <span aria-hidden="true">&times;</span>
                    </button>
                  </div>
                  <div class="login-modal forgotyour">
                    <h2>Forgot Your Password</h2>
                    <h5>Provide Your account's email for which you want to reset password</h5>
                     <form onSubmit={handleSubmit}>
                      <div class="form-group input-group">
                        <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Email Address"/>
                        <div class="input-group-append">
                          <span class="input-group-text"><i class="fa fa-envelope-o" aria-hidden="true"></i></span>
                        </div>
                      </div>
                      <span style={{ color: "red", fontSize: 12 }}>
                                    {errors.email}
                                </span>

                      <div class="row">
                        <div class="col-md-12">
                          <div class="sign-in mb-5">
                            <button type="submit">Submit</button>
                          </div>
                        </div>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </Modal>
            <FooterComponents />
            {/* login Modal */}

        </div>
        </>
    );
}

export default ForgetPasswordComponents;
