import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios'
import '../../Utils/style.css';
import "../../Utils/css/header.css"
import "../../Utils/css/bootstrap.min.css"
import "../../Utils/css/bootstrap-datepicker.css"
import profile from "../../images/profile.png"
import pencil from "../../images/pencil.png"
import DatePicker from "react-datepicker";
import { Network } from '../../Services/Api';
import { ToastContainer, toast } from 'react-toastify';
import "react-datepicker/dist/react-datepicker.css";
import HeaderComponents from "../../Components/header"
import FooterComponents from "../../Components/footer"



function BecomeHostComponents(props) {


  return (
    <div>
      <HeaderComponents flag={true} as={props} />
      <section class="home-profile">
        <div class="container">
          <div class="row">
            <div class="col-lg-6 col-md-10 col-10 mx-auto profile-sec">
              <form onSubmit={handleSubmit}>
                <figure class="profile">
                  {
                    profileImage ? 
                    <img src={profile} class="img-fluid" alt="" />
                    :
                    <img src={`http://nodeserver.mydevfactory.com:1443/${profileImage}`} class="img-fluid" alt=""  style={{width:100,height:100,borderRadius:50}}/>
                  }

                </figure>
                <img src={pencil} class="img-fluid pencil" alt="" onClick={() => handleClick()} />
                <input
                  type="file"
                  style={{ display: "none" }}
                  className="form-control"
                  name="file"
                  onChange={handleChange} 
                  ref={inputEl}
                // value=""
                />
                <div class="form-group input-group">
                  <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="First Name" onChange={(event) => setfirstName(event.target.value)} value={firstName} name="firstName" />
                  <div class="input-group-append">
                    <span class="input-group-text"><i class="fa fa-user-o" aria-hidden="true"></i></span>
                  </div>
                </div>
                <span style={{ color: "red", fontSize: 12 }}>
                  {errors.firstName}
                </span>
                <div class="form-group input-group">
                  <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Last Name" onChange={(event) => setlastName(event.target.value)} value={lastName} name="lastName" />
                  <div class="input-group-append">
                    <span class="input-group-text"><i class="fa fa-user-o" aria-hidden="true"></i></span>
                  </div>
                </div>
                <span style={{ color: "red", fontSize: 12 }}>
                  {errors.lastName}
                </span>
                <div class="form-group">
                  <div class="dates input-group">
                    {/* <input type="text" class="form-control" id="usr1" name="event_date" placeholder="D.O.B" autocomplete="off" /> */}
                    <div className="form-control">
                      <DatePicker
                        selected={startDate}
                        onChange={date => setStartDate(date)}
                        customInput={<ExampleCustomInput />}
                      />
                    </div>

                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-calendar-o fa-1x" aria-hidden="true"></i></span>
                    </div>
                  </div>
                </div>
                <div class="form-group input-group">
                  <select onChange={(e) => handleGender(e)} name="genderName">
                    {
                      Gender.map((item) => {
                        return (
                          <option value={item.value} name="genderName">{item.name}</option>
                        )
                      })
                    }
                  </select>
                </div>
                <div class="form-group input-group">
                  <select onChange={(e) => handleCountry(e)} name="countryName">
                    {
                      country.map((item) => {
                        return (
                          <option value={item.value} name="countryName">{item.name}</option>
                        )
                      })
                    }
                  </select>
                </div>

                <div class="form-group">
                  <textarea class="form-control" id="exampleFormControlTextarea1" rows="2" placeholder="Type your comment" onChange={(event) => setAbout(event.target.value)} value={about} name="about"></textarea>
                </div>
                <span style={{ color: "red", fontSize: 12 }}>
                  {errors.about}
                </span>
                <div class="row">
                  <div class="col-md-12">
                    <div class="save-btn">
                      <button type="submit">Save</button>
                    </div>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>


      <FooterComponents />
    </div >
  );
}

export default BecomeHostComponents;
