import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import LoginScreen from "../Components/login"
import SignUpComponents from "../Components/signUp"
import ForgetPasswordComponents from "../Components/forgetPassword"
import HomeScreen from './../Screens/Home';         //....Home components.....
import NotFound from "./../../Error"


export const currentURL = 'projects/suvendu/robins/mondongo';


function RouterScreen(){
    const userdata = useSelector((state) => state.userdata);
    const [userMe, setUser] = useState(null);

    useEffect(() => {
        let user = userdata && userdata._id ? true : false
        console.log("userMe===>", user);
        setUser(user);
        console.log("USerData", userdata);
        const userLocal = JSON.parse(localStorage.getItem('user'));
        console.log("userData after login--->", userLocal)
        let userD = userLocal && userLocal._id ? true : false
        setUser(userD);


    }, [userdata]);


    return (

        <Router basename={currentURL}>
            <div>
                {
                    userMe ?
                        <Switch>
                           <Route path='/' component={HomeScreen} exact />     
                            <Route component={NotFound} />
                        </Switch >

                        :

                        <Switch >
                           <Route path='/' component={HomeScreen} exact />  
                            <Route path='/login' component={LoginScreen} />
                            <Route path='/signup' component={SignUpComponents} />
                            <Route path='/forgetPassword' component={ForgetPasswordComponents} /> 
                            <Route component={NotFound} />
                        </Switch >
                }

            </div>
        </Router>
    );
}

export default RouterScreen;
