import React, { useState } from 'react';
import Modal from "react-bootstrap/Modal";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory
} from "react-router-dom";
import HeaderComponents from "../Components/header"
import FooterComponents from "../Components/footer"
import ReCAPTCHA from "react-google-recaptcha";
import '../Utils/style.css';
import "../Utils/css/header.css"
import "../Utils/css/bootstrap.min.css"
import "../Utils/css/bootstrap-datepicker.css"
import Imnot from "../images/imnot.png"
import { Network } from './../Services/Api';
import { useDispatch } from 'react-redux';
import { loginUser } from "./../Redux/Actions/auth"
import MyLoader from "../Components/Comman/loader"
import { ToastContainer, toast } from 'react-toastify';




function LoginComponents(props) {

  const [input, setInput] = useState({})
  const [errors, setError] = useState({})
  const dispatch = useDispatch()
  const [email, setEmail] = useState('')
  const [otp, setOtp] = useState('')
  const [otpShow, setOtpShow] = useState(false)
  const [loader, setLoader] = useState(false)
  const [captchaValue, setCaptcha] = useState("")



  const validate = () => {
      let input1 = input;
      let errors = {};
      let isValid = true;

      if (!input1["email"]) {
          isValid = false;
          errors["email"] = "Please enter your email Address.";
      }

      if (typeof input1["email"] !== "undefined") {

          var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
          if (!pattern.test(input1["email"])) {
              isValid = false;
              errors["email"] = "Please enter valid email address.";
          }
      }
      if (!input1["password"]) {
          isValid = false;
          errors["password"] = "Please enter your password.";
      }
      if (captchaValue == "") {
        isValid = false;
        errors["captcha"] = "Please select captcha.";
    }
      setError(errors)

      return isValid;
  }
  //.........for on change mether.............

  const handleChange = (event) => {
      let input1 = input;
      input1[event.target.name] = event.target.value;
      setInput(input1)
  }

  //.........Login submit............

  const handleSubmit = (event) => {
    setLoader(true)
      if (validate()) {
          var obj = {
              "email": input['email'],
              "password": input['password'],
              "deviceToken": "123456"
          }
          // console.log("obj submit for log in ----->", obj)

          Network('user/login', 'post', obj)
              .then(async (res) => {
                  // console.log("res success login--->", res);
                  if (res.status == true) {
                    toast.success(res.message)
                     setLoader(false)
                      localStorage.setItem('user', JSON.stringify(res));
                      dispatch(loginUser(res.result))
                      props.history.push("/")
                   } else if (res.status == false) {
                      toast.error(res.message)
                   } else {
                      // setSuccess(true)
                  }
              })
              .catch((error) => {
                setLoader(false)
                  console.log("error===>", error)
              });

      }

      event.preventDefault();
  }


  const onChange = (value)=>{
     setCaptcha(value)

    console.log("Captcha value:", value);
  }

  return (
    <>
      <div>
        {/* login Modal */}
        <HeaderComponents />
        <MyLoader active={true}>
        <Modal class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true" show={true}>
          <div class="modal-dialog" role="document">
            <div class="modal-content">
              <div class="modal-header">
                <button type="button" class="close" data-dismiss="modal" aria-label="Close" onClick={() => props.history.push("/")} >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div class="login-modal">
                <h2>Sign In!</h2>
                <h5>Hi there! Nice to see you again</h5>
                <form onSubmit={handleSubmit}>
                  <div class="form-group input-group">
                    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Email Address" onChange={handleChange} name="email"/>
                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-envelope-o" aria-hidden="true"></i></span>
                    </div>
                  </div>
                  <span style={{ color: "red", fontSize: 12 }}>
                                    {errors.email}
                                </span>

                  <div class="form-group input-group">
                    <input type="password" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Password" onChange={handleChange} name="password"/>
                    <div class="input-group-append">
                      <span class="input-group-text"><i class="fa fa-eye" aria-hidden="true"></i></span>
                    </div>
                  </div>
                  <span style={{ color: "red", fontSize: 12 }}>
                                    {errors.password}
                                </span>

                  <div class="row">
                    <div class="col-md-12">
                      <Link to="/ForgetPassword">
                        <h3><a href="#">Forgot Password?</a></h3>
                      </Link>
                      <ReCAPTCHA
                        style={{marginBottom:20,alignItems:"center"}}
                        sitekey="6LevjSUaAAAAAK3cWe9skemHtug2_5k16_JDRyDc"
                        onChange={onChange}
                      />
                      <span style={{ color: "red", fontSize: 12 }}>
                                    {errors.captcha}
                                </span>     
                      <div class="sign-in">
                        <button type="submit">Sign In</button>
                      </div>
                      <h4>Don’t have an Account? <a href="#">Create an Account</a></h4>
                    </div>
                  </div>
                </form>
              </div>
            </div>

          </div>
        </Modal>
        <FooterComponents />
        {/* login Modal */}
        </MyLoader>
      </div>
    </>
  );
}

export default LoginComponents;
