import React, { useState, useEffect } from 'react';
import Modal from "react-bootstrap/Modal";
import './../../../App.css'

export const ToastComponent = (props) => {
    useEffect(() => {
        setTimeout(() => {
            setShow(false)
        }, 3000);

    }, []);

    const [show, setShow] = useState(props.show);

    const handleClose = () => setShow(false)

    const handleShow = () => setShow(true);



    return (
        <>
        {
            console.log(props.show, "props.type-->",props.type)
        }
            {props.type ?
                <Modal show={show} onHide={handleClose} className="special_modal">
                    <Modal.Header closeButton>
                        <Modal.Title>{props.HeaderTitle}</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>{props.title}</Modal.Body>
                </Modal>

                :

                <Modal show={show} onHide={handleClose} className="special_modal1">
                    <Modal.Header closeButton>
                        <Modal.Title>{props.HeaderTitle}</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>{props.title}</Modal.Body>
                </Modal>
            }
        </>
    )
}

