import React, { useState, useEffect } from 'react';
import './../../../Utils/Dashboard/style.css';
import './../../../Utils/Dashboard/css/header.css';
import './../../../Utils/Dashboard/css/bootstrap.min.css';
import calendar from './../../../images/calendar.png'
import vector from './../../../images/vector.png'
import arrow_vector from './../../../images/arrow-vector.png'
import FooterComponent from './../../../Components/Comman/footer';
import HeaderComponent from './../Header/index'
import SideMenuComponent from "./../../../Components/Comman/SideMenu";
import profile from './../../../images/christian-buehner-DItYlc26zVI-unsplash 1.png'
import countryCode from './../../../Components/Comman/country.json'
import { Network } from './../../../Services/Api';


function ProfilesComponent(props) {
    const [user, setUser] = useState({})
    const [input, setInput] = useState({})
    const [errors, setError] = useState({})
    const [userDetails, setUserDetails] = useState({})
    const [firstName, setfirstName] = useState("")
    const [lastName, setlastName] = useState("")
    const [date, setdate] = useState("")
    const [gender, setgender] = useState("")
    const [country, setcountry] = useState("")
    const [sport, setsport] = useState("")
    const [weight, setweight] = useState("")
    const [weightUnit, setweightUnit] = useState('kg')
    const [heigth, setheigth] = useState('')
    const [heigthUnit, setheigthUnit] = useState("feet")
    const [injuries, setinjuries] = useState('')
    const [Describe, setDescribe] = useState('')


    // const validate = () => {
    //     let input1 = input;
    //     let errors = {};
    //     let isValid = true;

    //     //         Describe: "56"
    //     // country: "Afghanistan"
    //     // firstName: "r"
    //     // heigth: "20"
    //     // lastName: "e"
    //     // optradio: "on"
    //     // sport: "baseball"
    //     // weight: "10"
    //     // weightName: "KG"

    //     if (!input1["firstName"]) {
    //         isValid = false;
    //         errors["firstName"] = "Please enter your first name.";
    //     }

    //     if (!input1["lastName"]) {
    //         isValid = false;
    //         errors["lastName"] = "Please enter your last name.";
    //     }

    //     if (!input1["country"]) {
    //         isValid = false;
    //         errors["country"] = "Please select your country.";
    //     }

    //     if (!input1["Describe"]) {
    //         isValid = false;
    //         errors["Describe"] = "Please enter your Describe.";
    //     }

    //     if (!input1["weight"]) {
    //         isValid = false;
    //         errors["weight"] = "Please enter your weight.";
    //     }

    //     if (!input1["heigth"]) {
    //         isValid = false;
    //         errors["heigth"] = "Please enter your heigth.";
    //     }

    //     if (!input1["optradio"]) {
    //         isValid = false;
    //         errors["optradio"] = "Please select your INJURIES PAST/PRESENT.";
    //     }

    //     if (!input1["heigthName"]) {
    //         isValid = false;
    //         errors["heigthName"] = "Please select your heigth unit.";
    //     }


    //     setError(errors)

    //     return isValid;
    // }

    const handleChange = (event) => {
        let input1 = input;
        input1[event.target.name] = event.target.value;
        console.log(input1)
        setInput(input1)
    }


    useEffect(() => {

        const user = JSON.parse(localStorage.getItem('user'));
        getUserDetails(user)
        setUser(user)
        console.log("userData--->", user)



    }, []);

    const getUserDetails = (user) => {
        const obj = {
            'authToken': user.authtoken
        }

        Network('view-profile?_id=' + user._id, 'get', obj)
            .then(async (res) => {
                console.log("res profiles details--->", res);
                if (res.response_code == 2000) {
                    // const [lastName, setlastName] = useState("")
                    // const [date, setdate] = useState("")
                    // const [gender, setgender] = useState("")
                    // const [country, setcountry] = useState("")
                    // const [sport, setsport] = useState("")
                    // const [weight, setweight] = useState("")
                    // const [weightUnit, setweightUnit] = useState('')
                    // const [heigth, setheigth] = useState('')
                    // const [heigthUnit, setheigthUnit] = useState("")
                    // const [injuries, setinjuries] = useState('')
                    // const [Describe, setDescribe = useState('')]


                    setfirstName(res.response_data.fname)
                    setlastName(res.response_data.lname)
                    setdate(res.response_data.dob)
                    setcountry(res.response_data.country)
                    setgender(res.response_data.gender)
                    setinjuries(res.response_data.injury_details)
                    setheigth(res.response_data.height.height)
                    setweight(res.response_data.weight.weight)
                    setheigthUnit(res.response_data.height.height.type)
                    setheigthUnit(res.response_data.weight.weight.type)
                    setDescribe(res.response_data.injury_details)

                    // setweight(res.response_data.weight)
                    // setlastName(res.response_data.fname)
                    // setlastName(res.response_data.fname)
                    // setlastName(res.response_data.fname)
                    // setlastName(res.response_data.fname)
                    // setlastName(res.response_data.fname)

                }
            })
            .catch((error) => {
                console.log("error===>", error)
            });
    }



    const handleSubmit = (event) => {


        const obj = {
            "_id": user._id,
            "fname": firstName,
            "lname": lastName,
            "dob": date,  // dob format : 07-03-95  [mm-dd-yy]
            "phone_no": input['phone_no'],
            "country": country,
            "gender": gender,
            "sports": sport,
            "height": { "height": heigth, "type": heigthUnit },
            "weight": { "weight": weight, "type": weightUnit },
            "injuries": injuries,
            "injury_details": Describe,
            'authToken': user.authtoken
        }

        console.log("obj submit--->", obj);


        Network('edit-profile', 'post', obj)
            .then(async (res) => {
                console.log("res success register--->", res);
                if (res.response_code == 2000) {
                    // setOtpShow(true)
                }
            })
            .catch((error) => {
                console.log("error===>", error)
            });
        event.preventDefault();
    }


    return (
        <>
            <HeaderComponent />

            <section class="admin-home">
                <div class="container">
                    <div class="row" id="mob-menu-side">
                        <div class="col-lg-10 col-8">
                            <h2>My Account</h2>
                        </div>
                        {/* <!-- Only For Mobile --> */}
                        <div class="col-lg-2 col-4" id="threebar">
                            <a href="#"><i class="fa fa-bars mob-menu threebar"></i></a>
                        </div>
                    </div>
                    <div class="row">
                        <SideMenuComponent />
                        <div class="col-lg-9 col-10 home-right">
                            <div class="goal-sec-one my-profile">
                                <div class="row">
                                    <div class="col-md-8 gta-sr gta-srr">
                                        <h6>My Profile</h6>
                                        {user ?
                                            <img src={user.profile_image} class="img-fluid" alt="" style={{ width: 150, height: 150, borderRadius: 150 / 2 }} />
                                            :
                                            <img src={profile} class="img-fluid" alt="" />
                                        }
                                        <div class="edit-profile">
                                            <a href="#"><i class="fa fa-pencil-square" aria-hidden="true"></i></a>
                                        </div>
                                    </div>
                                </div>
                                <form onSubmit={handleSubmit}>
                                    <div class="row">
                                        <div class="col-md-6 mx-auto">
                                            <div class="form-group">
                                                <input type="text" value={firstName} name="firstName" onChange={(event) => setfirstName(event.target.value)} class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="First Name" />
                                            </div>
                                        </div>
                                        <div class="col-md-6 mx-auto">
                                            <div class="form-group">
                                                <input type="text" value={lastName} name="lastName" onChange={(event) => setlastName(event.target.value)} class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Last Name" />
                                            </div>
                                        </div>
                                    </div>
                                    <div class="row">
                                        <div class="col-md-6 mx-auto change-language">
                                            <div class="dates input-group">
                                                <input type="text" name="date" value={date} onChange={(event) => setdate(event.target.value)} class="form-control" id="usr1" name="event_date" placeholder="Date of birth" autocomplete="off" />
                                                <div class="input-group-append">
                                                    <img src={calendar} class="img-fluid" alt="" />
                                                </div>
                                            </div>
                                        </div>
                                        <div class="col-md-6 mx-auto">
                                            <select name="gender" value={gender} onChange={(event) => setgender(event.target.value)}>
                                                <option>Gender</option>
                                                <option value="male">Male</option>
                                                <option value="female">Female</option>
                                            </select>
                                        </div>
                                    </div>

                                    <div class="row ytyu">
                                        <div class="col-md-6 mx-auto">
                                            <select onChange={(event) => setcountry(event.target.value)} value={country} name="country">
                                                <option>Country</option>
                                                {
                                                    countryCode.map((item) => {
                                                        return (
                                                            <option value={item.name} name="numberCode">{item.name}</option>
                                                        )
                                                    })
                                                }
                                            </select>
                                        </div>
                                        <div class="col-md-6 mx-auto">
                                            <select onChange={(event) => setsport(event.target.value)} value={sport} name="sport">
                                                <option>Sport</option>
                                                <option value="basketball">Basketball</option>
                                                <option value="tennis">Tennis</option>
                                                <option value="baseball">Baseball</option>
                                                <option value="golf">Golf</option>
                                                <option value="soccer">Soccer</option>
                                                <option value="running">Running</option>
                                                <option value="volleyball">Volleyball</option>
                                                <option value="football">Football</option>
                                                <option value="cricket">Cricket</option>
                                                <option value="pool">Pool</option>
                                            </select>
                                        </div>
                                    </div>
                                    <div class="row">
                                        <div class="col-md-6 mx-auto">
                                            <div class="row">
                                                <div class="col-md-9 col-8 no-pad">
                                                    <div class="form-group">
                                                        <input type="text" name='weight' value={weight} onChange={(event) => setweight(event.target.value)} class="form-control" id="exampleInputPassword1" placeholder="Weight" />
                                                    </div>
                                                </div>
                                                <div class="col-md-3 col-4 no-pad-two">
                                                    <select name='weightName' value={weightUnit} onChange={(event) => setweightUnit(event.target.value)}>
                                                        <option value="kg">KG</option>
                                                        <option value="pw">PW</option>
                                                    </select>
                                                </div>
                                            </div>
                                        </div>
                                        <div class="col-md-6 mx-auto">
                                            <div class="row">
                                                <div class="col-md-9 col-8 no-pad">
                                                    <div class="form-group">
                                                        <input type="text" name="heigth" value={heigth} onChange={(event) => setheigth(event.target.value)} class="form-control" id="exampleInputPassword1" placeholder="Height" />
                                                    </div>
                                                </div>
                                                <div class="col-md-3 col-4 no-pad-two">
                                                    <select value={heigthUnit} onChange={(event) => setheigthUnit(event.target.value)} name="heigthName">
                                                        <option value="feet">FEET</option>
                                                        <option value="inc">INC</option>
                                                    </select>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                    <div class="row">
                                        <div class="col-md-12">
                                            <div class="row">
                                                <div class="col-md-3">
                                                    <h5>Injuries past/present</h5>
                                                </div>
                                                <div class="col-md-9">
                                                    <div class="radio">
                                                        <label><input type="radio" name="injuries" value={injuries} onChange={(event) => setinjuries(event.target.value)} checked />Yes</label>
                                                        <label class="ml-4"><input type="radio" value={injuries} onChange={(event) => setinjuries(event.target.value)} name="injuries" />No</label>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                        <div class="col-md-12">
                                            <div class="form-group">
                                                <textarea class="form-control" value={Describe} onChange={(event) => setDescribe(event.target.value)} id="exampleFormControlTextarea1" rows="3" placeholder="Describe" name="Describe"></textarea>
                                            </div>
                                        </div>
                                    </div>
                                    <div class="row">
                                        <div class="col-md-12 my-profile-btn">
                                            <div class="subscribe-son mt-4">
                                                <button type="submit" >Save <img src={vector} class="img-fluid" alt="" /></button>
                                            </div>
                                            <div class="subscribe-so mt-4 ml-4 mr-4">
                                                <button type="submit" onClick={() => props.history.push({
                                                    pathname: '/resetPassword',
                                                    // search: '?the=search',
                                                    state: { id: user._id }
                                                })}>CHANGE PASSWORD <img src={arrow_vector} class="img-fluid" alt="" /></button>
                                            </div>
                                            <div class="subscribe-son mt-4">
                                                <button type="submit">PAYMENT METHOD <img src={vector} class="img-fluid" alt="" /></button>
                                            </div>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            <FooterComponent />
        </>
    )
}

export default ProfilesComponent;
