import React, { useState } from 'react';
import Modal from "react-bootstrap/Modal";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory
} from "react-router-dom";
import '../../Utils/css/style.css';
import '../../Utils/css/responsive.css';
import "../../Utils/css/bootstrap.min.css"
import "../../Utils/css/bootstrap-datepicker.css"
import modalClose from "../images/modal-close.png"
import { Network } from './../Services/Api';
import { useDispatch } from 'react-redux';
import { loginUser } from "./../Redux/Actions/auth"
import MyLoader from "../Components/Comman/loader"
import { ToastContainer, toast } from 'react-toastify';




function LoginComponents(props) {

  const [input, setInput] = useState({})
  const [errors, setError] = useState({})
  const dispatch = useDispatch()
  const [email, setEmail] = useState('')
  const [otp, setOtp] = useState('')
  const [otpShow, setOtpShow] = useState(false)
  const [loader, setLoader] = useState(false)
  const [captchaValue, setCaptcha] = useState("")



  const validate = () => {
    let input1 = input;
    let errors = {};
    let isValid = true;

    if (!input1["email"]) {
      isValid = false;
      errors["email"] = "Please enter your email Address.";
    }

    if (typeof input1["email"] !== "undefined") {

      var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
      if (!pattern.test(input1["email"])) {
        isValid = false;
        errors["email"] = "Please enter valid email address.";
      }
    }
    if (!input1["password"]) {
      isValid = false;
      errors["password"] = "Please enter your password.";
    }
    if (captchaValue == "") {
      isValid = false;
      errors["captcha"] = "Please select captcha.";
    }
    setError(errors)

    return isValid;
  }
  //.........for on change mether.............

  const handleChange = (event) => {
    let input1 = input;
    input1[event.target.name] = event.target.value;
    setInput(input1)
  }

  //.........Login submit............

  const handleSubmit = (event) => {
    setLoader(true)
    if (validate()) {
      var obj = {
        "email": input['email'],
        "password": input['password'],
        "deviceToken": "123456"
      }
      // console.log("obj submit for log in ----->", obj)

      Network('user/login', 'post', obj)
        .then(async (res) => {
          // console.log("res success login--->", res);
          if (res.status == true) {
            toast.success(res.message)
            setLoader(false)
            localStorage.setItem('user', JSON.stringify(res));
            dispatch(loginUser(res.result))
            props.history.push("/")
          } else if (res.status == false) {
            toast.error(res.message)
          } else {
            // setSuccess(true)
          }
        })
        .catch((error) => {
          setLoader(false)
          console.log("error===>", error)
        });

    }

    event.preventDefault();
  }


  const onChange = (value) => {
    setCaptcha(value)

    console.log("Captcha value:", value);
  }

  return (
    <>
      <div>
        {/* login Modal */}
        <MyLoader active={true}>
          <Modal class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true" show={true}>
            <div class="modal" id="myModallogin">
              <div class="modal-dialog custom-modal">
                <div class="modal-content">

                  {/* <!-- Modal Header --> */}
                  <div class="modal-header">
                    <h4 class="modal-title">Login</h4>
                    <button type="button" class="close" data-dismiss="modal">
                      <img src={modalClose} alt="" />
                    </button>
                  </div>

                  {/* <!-- Modal body --> */}
                  <div class="modal-body">
                    <form>
                      <div class="form-group">
                        <label>Email</label>
                        <input type="email" class="form-control" />
                      </div>
                      <div class="form-group">
                        <label>Password</label>
                        <input type="password" class="form-control" />
                      </div>
                      <div class="btn-set">
                        <button type="submit" class="btn btn-deflt">Submit</button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>

          </Modal>
          {/* login Modal */}
        </MyLoader>
      </div>
    </>
  );
}

export default LoginComponents;
