import React, { useState } from 'react';
import Modal from "react-bootstrap/Modal";
import '../Utils/style.css';
import "../Utils/css/header.css"
import "../Utils/css/bootstrap.min.css"
import OtpInput from 'react-otp-input';

function OtpComponents(props) {
    const [otp, setOtp] = useState('')


    const handleChange = otp => {
        props.handleChange(otp)
        setOtp(otp)
    }

    return (
        <>
            <Modal show={props.show} className="modal fade" id="exampleModal-two" aria-labelledby="exampleModalLabel" role="dialog">
                <div className="modal-header">
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close" onClick={() =>props.close()}>
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div className="modal-body">
                    <h2>verify-otp</h2>
                    <div style={{ alignItems: 'center', display: "flex", justifyContent: 'center', height: 200 }}>
                        <OtpInput
                            value={otp}
                            onChange={handleChange}
                            shouldAutoFocus={false}
                            numInputs={4}
                            placeholder={"000000"}
                            containerStyle={{ borderColor: 'red', borderWidth: 2, borderRadius: 10 }}
                            inputStyle={{  
                                width: '3rem',  
                                height: '3rem',  
                                margin: '20px 1rem',  
                                fontSize: '2rem',  
                                borderRadius: 4,  
                                border: '2px solid rgba(0,0,0,0.3)',  
                              }}  
                            separator={<span style={{fontSize:20}}> - </span>}
                            />


                    </div>
                    <div className="sign-in sign-two">
                        {/* <button type="submit" onClick={()=> props.onSubmitOtp()}>Save</button> */}
                        <button onClick={()=> props.onSubmitOtp()}>Save</button>
                    </div>
                </div>
            </Modal>
        </>
    )
}
export default OtpComponents;
