import React, { useState, useEffect } from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useHistory,
    HashRouter
} from "react-router-dom";
import { DropdownButton, DropdownType, Dropdown, SplitButton, ButtonGroup } from "react-bootstrap"
import { useSelector,useDispatch } from 'react-redux';
import { logoutUser } from "../Redux/Actions/auth"
import '../Utils/style.css';
import "../Utils/css/header.css"
import "../Utils/css/bootstrap.min.css"
import "../Utils/css/bootstrap-datepicker.css"
import Banner from "../images/Banner.jpg"
import Logo from "../images/logo.png"
import Layer from "../images/Layer 543.png"



function HeaderComponents(props) {
    const dispatch = useDispatch()
    const userdata = useSelector((state) => state.userdata);
    const [userMe, setUser] = useState(null);
    const [user, setUserData] = useState({})


    useEffect(() => {
        let user = userdata && userdata._id ? true : false
        // console.log("userMe===>", user);
        setUser(user);
        // console.log("USerData", userdata);
        const userLocal = JSON.parse(localStorage.getItem('user'));
        // console.log("userData after login--->", userLocal)
        let userD = userLocal && userLocal.result._id ? true : false
        setUser(userD);
        setUserData(userLocal)

    }, [userdata]);

    const handleLogout = () => {
        alert("gh")
        console.log("pruyuuuuuu",props)

        dispatch(logoutUser(null))
        const userLocal = JSON.parse(localStorage.removeItem('user'));
        setUserData(userLocal)
        props.as.history.push("/login")


    }
    return (
        <div>
            {/* {console.log("1111111111", userMe)} */}
            {/* header   */}
            <section class={props.flag == true ? "home-menu-sec-profile" : "home-menu-sec"} style={{ backgroundImage: `url(${Banner})`}}>
                <header id="home-menu">
                    <div class="container-fluid" id="navv">
                        <div class="row">
                            <div class="col-lg-3 col-md-10 col-10" id="bannar-logo">
                                <a href="index.html">
                                <figure>
                                    <img src={Logo} class="img-fluid" alt="" />
                                 </figure>
                                </a>
                                <ul class="ulone-two">
                                <li class="bor"><a href="#" data-toggle="modal" data-target="#exampleModal-four">$</a></li>
                               <li class="bor"><a href="#"><img src={Layer} class="img-fluid" alt="" style={{width:40,height:40,borderRadius:20,alignItems:"center",justifyContent:"center"}} /></a></li>

                                </ul>
                            </div>


                            <div class="col-2 col-md-2 text-right show--mobile--only">
                                <ul>
                                    <li class="mob-only-menu text-right">
                                        <a href="#">
                                            <i class="fa fa-bars mob-menu threebar"></i>
                                        </a>
                                    </li>
                                </ul>
                            </div>

                            <div class="col-lg-5 col-md-12 col-12 mob-manubarr menu-bg">
                                <ul class="ulone">
                                    <li><a href="#">Home</a></li>
                                    <li><a href="#">My Bookings</a></li>
                                    <li><a href="#">Help</a></li>
                                    <li><a href="#">Become Host</a></li>
                                </ul>
                            </div>
                            <div class="col-lg-4 col-md-12 col-12 mob-manubarr">
                                <ul class="ulone-two">

                                    <li>
                                        {
                                            userMe == true ?
                                              <Link to="/editProfile">
                                                <a href="#" ><img src={`${"http://nodeserver.mydevfactory.com:1443/"}${user.result.profile_url}`} class="img-fluid" alt="" style={{ width: 50, height: 50, marginTop: -10, borderRadius: 25,borderColor:"red",borderWidth:1 }}/></a>
                                             </Link>                                         
                                                :
                                                <Link to="/login" style={{ margin: 0, padding: 0 }}><a href="#"><i class="fa fa-lock" aria-hidden="true"></i>Sign in</a></Link>
                                        }

                                    </li>


                                    <li>
                                        {
                                            userMe == true ?
                                                <div className="mb-2" style={{ marginRight: 10, marginTop: -5 }}>

                                                    <Dropdown size="sm" drop="down">
                                                        <Dropdown.Toggle variant="light" id="dropdown-basic">
                                                            Hii,{user.result.firstName}
                                                        </Dropdown.Toggle>

                                                        <Dropdown.Menu >
                                                            <Dropdown.Item href="#/action-1" style={{ color: "red" }} onClick={handleLogout}>Logout</Dropdown.Item>
                                                        </Dropdown.Menu>
                                                    </Dropdown>
                                                </div>
                                                :
                                                <Link to="/signup" style={{ margin: 0, padding: 0 }}><a href="#"><i class="fa fa-user-o" aria-hidden="true"></i>Create account</a> </Link>
                                        }
                                    </li>
                                </ul>
                            </div>

                        </div>
                    </div>
                </header>
            </section>

            {/* header end */}
        </div >
    );
}

export default HeaderComponents;
