import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import HomeScreen from './../Screens/Home';         //....Home components.....
import NotFound from './../../Error';
import SubscribeScreen from './../Screens/Subscribe';
import WorkoutScreen from './../Screens/Workout';
import GoalScreen from './../Screens/Goal'
import Dashboard from './../Screens/Dashboard/Home/index';
import { useSelector } from 'react-redux';
import ForgotComponent from './../Components/Comman/forgot';
import ResetPassword from "./../Components/Comman/resetPass";
import LoginScreen from "./../Components/Comman/login";
import Signup from "./../Components/Comman/signup";
import Profiles from "./../Screens/Dashboard/Profiles";
import CurrentPlan from '../Screens/Dashboard/CurrentPlan'
import Subscription from "./../Screens/Dashboard/Subscription";
import Invite from "./../Screens/Dashboard/Invite";
import Workout from "./../Screens/Dashboard/Workout";
import Mypayment from "./../Screens/Dashboard/Mypayment";

export const currentURL = '/projects/suvendu/vivek/Fit4YourSports';


function RouterScreen() {
    const userdata = useSelector((state) => state.userdata);
    const [userMe, setUser] = useState(null);

    useEffect(() => {
        let user = userdata && userdata._id ? true : false
        console.log("userMe===>", user);
        setUser(user);
        console.log("USerData", userdata);
        const userLocal = JSON.parse(localStorage.getItem('user'));
        console.log("userData after login--->", userLocal)
        let userD = userLocal && userLocal._id ? true : false
        setUser(userD);


    }, [userdata]);


    return (
        <Router basename={currentURL}>
            <div>

                {
                    userMe ?
                        <Switch>
                            <Route path='/' component={Dashboard} exact />
                            <Route path='/profile' component={Profiles} />
                            <Route path='/currentPlan' component={CurrentPlan} />
                            <Route path='/subscription' component={Subscription} />
                            <Route path='/invite' component={Invite} />
                            <Route path='/workout' component={Workout} />
                            <Route path='/mypayment' component={Mypayment} />
                            <Route path='/resetPassword' component={ResetPassword} />


                            <Route component={NotFound} />
                        </Switch >

                        :

                        <Switch >
                            <Route path='/' component={HomeScreen} exact />
                            <Route path='/subscribe' component={SubscribeScreen} />
                            <Route path='/forgot' component={ForgotComponent} />
                            <Route path='/workout' component={WorkoutScreen} />
                            <Route path='/goal' component={GoalScreen} />
                            <Route path='/login' component={LoginScreen} />
                            <Route path='/signup' component={Signup} />
                            <Route path='/resetPassword' component={ResetPassword} />

                            <Route component={NotFound} />
                        </Switch >
                }

            </div>
        </Router>



    );
}

export default RouterScreen;
